  `view_groupbuy_count` int NULL DEFAULT NULL COMMENT '查看团购(次)',
  `collect_count` int NULL DEFAULT NULL COMMENT '收藏(次)',
  `share_count` int NULL DEFAULT NULL COMMENT '分享(次)',
