  UNIQUE INDEX `uk_date_shop`(`report_date` ASC, `shop_id` ASC) USING BTREE,
  INDEX `idx_shop_id`(`shop_id` ASC) USING BTREE,
  INDEX `idx_report_date`(`report_date` ASC) USING BTREE
